import { useState, useEffect } from "react";
import useInput from "../hooks/use.input";

const SimpleInput = (props) => {

  const {value: enteredName, hasError: nameInputHasError, valueChangeHandler: nameChangeHandler, inputBlurHandler: nameBlurHandler} = useInput(value=>value.trim()!=='')
  const {value: enteredEmail, hasError: emailInputHasError, valueChangeHandler: emailChangeHandler, inputBlurHandler: emailBlurHandler} = useInput(value=>{/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(value)})

  // const nameInputRef = useRef('')
  // const [enteredName, setEnteredName] = useState('');
  // const [enteredEmail, setEnteredEmail] = useState('');

  // const [enteredNameIsTouched, setEnteredNameIsTouched] = useState(false);
  // const [enteredEmailIsTouched, setEnteredEmailIsTouched] = useState(false);

  const [formIsValid, setFormIsValid] = useState(false);

  // const enteredEmailisValid = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(enteredEmail)
  // const enteredNameIsValid = enteredName.trim() !== "";

  // const nameInputIsInvalid = !enteredNameIsValid && enteredNameIsTouched;
  // const emailInputIsInvalid = !enteredEmailisValid && enteredEmailIsTouched;

  useEffect(() => {
    if (enteredNameIsValid && enteredEmailisValid) {
      setFormIsValid(true)
    } else {
      setFormIsValid(false)
    }
  }, [enteredNameIsValid, enteredEmailisValid])

  const ChangeHandler = (e) => {

    if (e.target.id === 'name') {
      setEnteredName(e.target.value);
    }

    if (e.target.id === 'email') {
      setEnteredEmail(e.target.value)
    }

  }

  const FormSubmissionHandler = (e) => {

    e.preventDefault();

    if (e.target.id==='name') {

      setEnteredNameIsTouched(true)

    } else if (e.target.id==='email') {
      setEnteredEmailIsTouched(true)
    }


    if (!enteredNameIsValid || !enteredEmailisValid) {
      return;
    }
    setEnteredName('');
    setEnteredEmail('');
    setEnteredNameIsTouched(false)
    setEnteredEmailIsTouched(false)
  }

  const BlurHandler = (e) => {
    if (e.target.id==='name') {
      setEnteredNameIsTouched(true)
    }
    if (e.target.id==='email') {
      setEnteredEmailIsTouched(true)
    }

  }

  const nameInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control'
  const emailInputClasses = emailInputIsInvalid ? 'form-control invalid' : 'form-control'
  return (
    <form onSubmit={FormSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input
          onBlur={BlurHandler}
          onChange={ChangeHandler}
          value={enteredName}
          type='text'
          id='name'
        />
        {nameInputIsInvalid && <p className="error-text">Invalid</p>}
      </div>
      <div className={emailInputClasses}>
        <label htmlFor='email'>Your Email</label>
        <input
          onBlur={BlurHandler}
          onChange={ChangeHandler}
          value={enteredEmail}
          type='email'
          id='email'
        />
        {emailInputIsInvalid && <p className="error-text">Invalid</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
