import { useState, useEffect } from "react";

const SimpleInput = (props) => {

  // const nameInputRef = useRef('')
  const [enteredName, setEnteredName] = useState('');
  const [enteredEmail, setEnteredEmail] = useState('');
  const [enteredNameIsTouched, setEnteredNameIsToName] = useState(false);

  const [formIsValid, setFormIsValid] = useState(false);

  const enteredNameIsValid = enteredName.trim() !== "";
  const enteredEmailIsValid = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(enteredEmail)
  const nameInputIsInvalid = !enteredNameIsValid && enteredInputIsToName;

  useEffect(() => {
    if (enteredNameIsValid && enteredEmailIsValid) {
      setFormIsValid(true)
    } else {
      setFormIsValid(false)
    }
  }, [enteredNameIsValid, enteredEmailIsValid])

  const ChangeHandler = (e) => {

    if (e.target.id === 'name') {
      setEnteredName(e.target.value);
    } else if (e.target.id === 'email') {
      setEnteredEmail(e.target.value)
    }
  }

  const FormSubmissionHandler = (e) => {

    e.preventDefault();

    setEnteredNameIsToName(true)

    if (!enteredNameIsValid || !enteredEmailIsValid) {
      return;
    }
    setEnteredName('');
    setEnteredEmail('')
    setEnteredNameIsToName(false)
  }

  const BlurHandler = () => {
    setEnteredNameIsToName(true)
  }

  const nameInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control'
  return (
    <form onSubmit={FormSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input
          onBlur={BlurHandler}
          onChange={ChangeHandler}
          value={enteredName}
          type='text'
          id='name'
        />
        {nameInputIsInvalid && <p className="error-text">Invalid</p>}
      </div>
      <div className={nameInputClasses}>
        <label htmlFor='email'>Your Email</label>
        <input
          onBlur={BlurHandler}
          onChange={ChangeHandler}
          value={enteredEmail}
          type='email'
          id='email'
        />
        {nameInputIsInvalid && <p className="error-text">Invalid</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
